sys_script_include.script:
var GetPriorityIncidentCountsAjax = Class.create();
GetPriorityIncidentCountsAjax.prototype = Object.extendsObject(AbstractAjaxProcessor, {

    getData: function() {
        var data = {};

        var ga = new GlideAggregate('incident');
        ga.addAggregate('COUNT');
        ga.addQuery('sys_created_on', '>=', 'javascript:gs.beginningOfLast12Months()');
        ga.groupBy('priority');
        ga.groupBy('MONTH(sys_created_on)');
        ga.query();

        while (ga.next()) {
            var month = ga.getValue('MONTH(sys_created_on)');
            var priority = ga.getValue('priority');
            var count = ga.getAggregate('COUNT');

            if (!data[month]) data[month] = {};
            data[month][priority] = count;
        }

        return new JSON().encode(data);
    }

});
