sys_script_include.script:
var GetPriorityIncidentStats = Class.create();
GetPriorityIncidentStats.prototype = {
    initialize: function() {},

    getMonthlyIncidentCounts: function() {
        var result = {};
        var gr = new GlideAggregate('incident');

        gr.addAggregate('COUNT');
        gr.addQuery('priority', '!=', ''); // Skip empty priorities
        gr.addEncodedQuery('sys_created_onONLast year@javascript:gs.beginningOfLastYear()@javascript:gs.endOfLastYear()');
        gr.groupBy('priority');
        gr.groupBy('sys_created_on');
        gr.query();

        while (gr.next()) {
            var month = gr.getValue('sys_created_on');
            var priority = gr.getDisplayValue('priority');
            var count = gr.getAggregate('COUNT');

            if (!result[month]) result[month] = {};
            result[month][priority] = count;
        }
        return result;
    },

    type: 'GetPriorityIncidentStats'
};
